mapper.select:
SELECT * FROM bearing_records WHERE 1=1

    AND transaction_type = #{transactionType}


    AND box_text = #{boxText}


    AND box_number = #{boxNumber}


    AND model  LIKE CONCAT('%',#{model},'%')


    AND customer  LIKE CONCAT('%',#{customer},'%')


    AND product_category = #{productCategory}


    AND outer_inner_ring = #{outerInnerRing}


    AND steel_type = #{steelType}


    AND steel_grade = #{steelGrade}


    AND depository = #{depository}


    AND state  LIKE CONCAT('%',#{state},'%')


    AND current_depository = #{currentDepository}


    AND DATE(time) BETWEEN #{startDate} AND #{endDate}

ORDER BY id DESC

    LIMIT #{begin},#{size}

SELECT COUNT(*) FROM bearing_records WHERE 1=1

    AND transaction_type = #{transactionType}


    AND box_text = #{boxText}


    AND box_number = #{boxNumber}


    AND model  LIKE CONCAT('%',#{model},'%')


    AND customer  LIKE CONCAT('%',#{customer},'%')


    AND product_category = #{productCategory}


    AND outer_inner_ring = #{outerInnerRing}


    AND steel_type = #{steelType}


    AND steel_grade = #{steelGrade}


    AND depository = #{depository}


    AND state  LIKE CONCAT('%',#{state},'%')


    AND DATE(time) BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(*) > 0 FROM bearing_records
WHERE box_text = #{boxText} AND box_number = #{boxNumber}
  AND depository = #{depository} AND quantity= #{quantity} AND iter = #{iter}
  AND transaction_type IN ('返库', '出库', '转入')

SELECT br.box_text, br.box_number, br.depository, br.customer, br.product_category, br.outer_inner_ring, br.steel_type, br.steel_grade, br.model, br.size, br.state, MAX(br.dissolve) as dissolve, SUM(CASE WHEN br.transaction_type IN ('入库', '转入') THEN br.quantity WHEN br.transaction_type IN ('出库', '转出', '返库') THEN -br.quantity END) as quantity, lr.last_recorded_time FROM bearing_records br JOIN ( SELECT box_text, box_number, MAX(time) as last_recorded_time FROM bearing_records WHERE transaction_type IN ('入库', '转入') AND bearing_records.time <= DATE_ADD(#{cutoffDate}, INTERVAL 1 DAY) GROUP BY box_text, box_number ) lr ON br.box_text = lr.box_text AND br.box_number = lr.box_number WHERE br.quantity > 0
AND br.time <= DATE_ADD(#{cutoffDate}, INTERVAL 1 DAY)
AND br.box_text = #{boxText}
AND br.box_number = #{boxNumber}
AND br.model LIKE CONCAT('%',#{model},'%')
AND br.customer LIKE CONCAT('%',#{customer},'%')
AND br.product_category = #{productCategory}
AND br.outer_inner_ring = #{outerInnerRing}
AND br.steel_type = #{steelType}
AND br.steel_grade = #{steelGrade}
AND br.depository = #{depository}
AND br.state = #{state}
GROUP BY br.box_text, br.box_number, br.depository, br.customer, br.product_category, br.outer_inner_ring, br.steel_type, br.steel_grade, br.quantity, br.model, br.size, br.state, lr.last_recorded_time ORDER BY last_recorded_time DESC
LIMIT #{begin},#{size}

WITH transaction_summary AS ( SELECT b.pair, br.model, br.outer_inner_ring, SUM(CASE WHEN br.transaction_type = '入库' THEN br.quantity ELSE 0 END) AS 入库, SUM(CASE WHEN br.transaction_type = '出库' THEN br.quantity ELSE 0 END) AS 出库, SUM(CASE WHEN br.transaction_type = '返库' THEN br.quantity ELSE 0 END) AS 返库, SUM(CASE WHEN br.transaction_type = '转入' THEN br.quantity ELSE 0 END) AS 转入, SUM(CASE WHEN br.transaction_type = '转出' THEN br.quantity ELSE 0 END) AS 转出 FROM bearing_records br JOIN bearings b ON br.box_text = b.box_text WHERE br.depository = #{depository} AND -- 使用depository作为过滤条件
br.state = #{state} AND
br.time > #{StartDate} AND br.time < DATE_ADD(#{EndDate},interval 1 DAY) GROUP BY b.pair, br.outer_inner_ring,br.model ), pre_start_date_inventory AS ( SELECT b.pair, br.outer_inner_ring, SUM(CASE WHEN br.transaction_type IN ('入库', '转入') THEN br.quantity WHEN br.transaction_type IN ('出库', '返库', '转出') THEN -br.quantity ELSE 0 END) AS 在库 FROM bearing_records br JOIN bearings b ON br.box_text = b.box_text WHERE br.depository = #{depository} AND -- 使用depository作为过滤条件 br.time < #{StartDate} GROUP BY b.pair, br.outer_inner_ring ), inventory AS ( SELECT b.pair, br.outer_inner_ring, SUM(CASE WHEN br.transaction_type IN ('入库', '转入') THEN br.quantity WHEN br.transaction_type IN ('出库','返库', '转出') THEN -br.quantity ELSE 0 END) AS 当前库存 FROM bearing_records br JOIN bearings b ON br.box_text = b.box_text WHERE br.depository = #{depository} AND br.time < DATE_ADD(#{EndDate},interval 1 DAY) GROUP BY b.pair, br.outer_inner_ring ) SELECT b.pair, MAX(b.model) AS 型号, MAX(b.steel_type) AS 钢厂, MAX(b.customer) AS 客户, MAX(b.size) AS 材料尺寸, MAX(CASE WHEN pre_inv.outer_inner_ring = 'LA' THEN pre_inv.在库 ELSE 0 END) AS 前月末在库_LA, MAX(CASE WHEN pre_inv.outer_inner_ring = 'LB' THEN pre_inv.在库 ELSE 0 END) AS 前月末在库_LB, SUM(CASE WHEN ts.outer_inner_ring = 'LA' THEN ts.入库 ELSE 0 END) AS 入库_LA, SUM(CASE WHEN ts.outer_inner_ring = 'LB' THEN ts.入库 ELSE 0 END) AS 入库_LB, SUM(CASE WHEN ts.outer_inner_ring = 'LA' THEN ts.出库 ELSE 0 END) AS 出库_LA, SUM(CASE WHEN ts.outer_inner_ring = 'LB' THEN ts.出库 ELSE 0 END) AS 出库_LB, SUM(CASE WHEN ts.outer_inner_ring = 'LA' THEN ts.返库 ELSE 0 END) AS 返库_LA, SUM(CASE WHEN ts.outer_inner_ring = 'LB' THEN ts.返库 ELSE 0 END) AS 返库_LB, SUM(CASE WHEN ts.outer_inner_ring = 'LA' THEN ts.转入 ELSE 0 END) AS 转入_LA, SUM(CASE WHEN ts.outer_inner_ring = 'LB' THEN ts.转入 ELSE 0 END) AS 转入_LB, SUM(CASE WHEN ts.outer_inner_ring = 'LA' THEN ts.转出 ELSE 0 END) AS 转出_LA, SUM(CASE WHEN ts.outer_inner_ring = 'LB' THEN ts.转出 ELSE 0 END) AS 转出_LB, MAX(CASE WHEN inv.outer_inner_ring = 'LA' THEN inv.当前库存 ELSE 0 END) AS 当月在库_LA, MAX(CASE WHEN inv.outer_inner_ring = 'LB' THEN inv.当前库存 ELSE 0 END) AS 当月在库_LB FROM bearings b LEFT JOIN transaction_summary ts ON b.pair = ts.pair AND b.outer_inner_ring = ts.outer_inner_ring and b.model=ts.model LEFT JOIN inventory inv ON b.pair = inv.pair AND b.outer_inner_ring = inv.outer_inner_ring LEFT JOIN pre_start_date_inventory pre_inv ON b.pair = pre_inv.pair AND b.outer_inner_ring = pre_inv.outer_inner_ring WHERE b.depository = #{depository} GROUP BY b.pair ORDER BY MAX(b.size);

SELECT COUNT(*) FROM bearing_records
WHERE box_text = #{boxText}
  AND box_number = #{boxNumber}
  AND depository = #{depository}
  AND iter = #{iter}

SELECT COALESCE(SUM(CASE WHEN transaction_type IN ('入库', '转入') THEN 1
                         WHEN transaction_type IN ('出库', '返库', '转出') THEN -1 ELSE 0 END), 0)
FROM bearing_records
WHERE box_text = #{boxText}
  AND box_number = #{boxNumber}
  AND depository = #{depository}
  AND iter = #{iter}

SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END
FROM bearing_records
WHERE box_text = #{boxText}
  AND box_number = #{boxNumber}
  AND iter = #{iter}
  AND transaction_type = '转入'

-- 查找匹配的转出和转入的记录 SELECT out_rec.id AS out_id, in_rec.id AS in_id, out_rec.box_text AS out_box_text, in_rec.box_text AS in_box_text, out_rec.box_number AS box_number, out_rec.quantity AS quantity, out_rec.current_depository AS out_depository, in_rec.current_depository AS in_depository, out_rec.time AS out_time, in_rec.time AS in_time FROM bearing_records AS out_rec INNER JOIN bearing_records AS in_rec ON REPLACE(out_rec.box_text, 'Z', '') = REPLACE(in_rec.box_text, 'Z', '') AND out_rec.box_number = in_rec.box_number AND out_rec.iter = in_rec.iter AND out_rec.time < in_rec.time WHERE out_rec.transaction_type = '转出' AND in_rec.transaction_type = '转入' AND out_rec.time BETWEEN #{startDate} AND #{endDate}
UNION ALL -- 查找有转出但没有匹配的转入的记录 SELECT out_rec.id AS out_id, NULL AS in_id, out_rec.box_text AS out_box_text, NULL AS in_box_text, out_rec.box_number AS box_number, out_rec.quantity AS quantity, out_rec.current_depository AS out_depository, NULL AS in_depository, out_rec.time AS out_time, NULL AS in_time FROM bearing_records AS out_rec LEFT JOIN bearing_records AS in_rec ON REPLACE(out_rec.box_text, 'Z', '') = REPLACE(in_rec.box_text, 'Z', '') AND out_rec.box_number = in_rec.box_number AND out_rec.iter = in_rec.iter AND out_rec.time < in_rec.time AND in_rec.transaction_type = '转入' WHERE out_rec.transaction_type = '转出' AND in_rec.id IS NULL AND out_rec.time BETWEEN #{startDate} AND #{endDate}
UNION ALL
-- 查找有转入但没有匹配的转出的记录 SELECT NULL AS out_id, in_rec.id AS in_id, NULL AS out_box_text, in_rec.box_text AS in_box_text, in_rec.box_number AS box_number, in_rec.quantity AS quantity, NULL AS out_depository, in_rec.current_depository AS in_depository, NULL AS out_time, in_rec.time AS in_time FROM bearing_records AS in_rec LEFT JOIN bearing_records AS out_rec ON REPLACE(out_rec.box_text, 'Z', '') = REPLACE(in_rec.box_text, 'Z', '') AND out_rec.box_number = in_rec.box_number AND out_rec.iter = in_rec.iter AND out_rec.time < in_rec.time AND out_rec.transaction_type = '转出' WHERE in_rec.transaction_type = '转入' AND out_rec.id IS NULL AND in_rec.time BETWEEN #{startDate} AND #{endDate}
ORDER BY COALESCE(out_time, in_time);

SELECT (
           SELECT COUNT(*) FROM bearing_records WHERE transaction_type = '转出'
       ) + (
           SELECT COUNT(*) FROM bearing_records WHERE transaction_type = '转入'
       ) AS total_count
