mapper.select:
SELECT * FROM bearing_records WHERE 1=1

    AND transaction_type = #{transactionType}


    AND box_text = #{boxText}


    AND box_number = #{boxNumber}


    AND customer = #{customer}


    AND product_category = #{productCategory}


    AND outer_inner_ring = #{outerInnerRing}


    AND steel_type = #{steelType}


    AND steel_grade = #{steelGrade}


    AND depository = #{depository}


    AND DATE(time) BETWEEN #{startDate} AND #{endDate}


    LIMIT #{begin},#{size}

SELECT COUNT(*) > 0 FROM bearing_records
WHERE box_text = #{boxText} AND box_number = #{boxNumber}
  AND depository = #{depository} AND quantity= #{quantity}
  AND transaction_type IN ('返库', '出库', '转入')

SELECT br.box_text, br.box_number, br.depository, br.customer, br.product_category, br.outer_inner_ring, br.steel_type, br.steel_grade, br.model, SUM(CASE WHEN br.transaction_type IN ('入库', '转入') THEN br.quantity WHEN br.transaction_type IN ('出库', '转出', '返库') THEN -br.quantity END) as quantity, lr.last_recorded_time FROM bearing_records br JOIN ( SELECT box_text, box_number, MAX(time) as last_recorded_time FROM bearing_records WHERE transaction_type IN ('入库', '转入') AND bearing_records.time <= DATE_ADD(#{cutoffDate}, INTERVAL 1 DAY) GROUP BY box_text, box_number ) lr ON br.box_text = lr.box_text AND br.box_number = lr.box_number WHERE br.quantity > 0
AND br.time <= DATE_ADD(#{cutoffDate}, INTERVAL 1 DAY)
AND br.box_text = #{boxText}
AND br.box_number = #{boxNumber}
AND br.model = #{model}
AND br.customer = #{customer}
AND br.product_category = #{productCategory}
AND br.outer_inner_ring = #{outerInnerRing}
AND br.steel_type = #{steelType}
AND br.steel_grade = #{steelGrade}
AND br.depository = #{depository}
GROUP BY br.box_text, br.box_number, br.depository, br.customer, br.product_category, br.outer_inner_ring, br.steel_type, br.steel_grade, br.quantity, br.model, lr.last_recorded_time
LIMIT #{begin},#{size}
