mapper.update:
UPDATE product_ids
SET is_stocked = #{isStocked}
WHERE box_text = #{boxText} AND box_number = #{boxNumber} AND depository_id = #{depositoryId} AND iter = #{iter}

mapper.select:
SELECT is_stocked
FROM product_ids
WHERE box_text = #{boxText} AND box_number = #{boxNumber} AND depository_id = #{depositoryId} AND iter = #{iter}

mapper.update:
UPDATE product_ids
SET quantity = #{quantity}
WHERE box_text = #{boxText} AND box_number = #{boxNumber} AND depository_id = #{depositoryId} AND iter = #{iter}
